package chap02;

public class Sample01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char chr ='A';
		System.out.println("charactor : "+chr); // 문자 A 출력
		System.out.println("Encoding : "+(int)chr); // A를 숫자로 인코딩
		System.out.println("Decoding : "+(char)65); //65를 문자로 디코딩
	}

}
